import React, { useState, useEffect, memo, TouchableOpacity } from "react";
import axios from "axios";
import PurpleCat from "./images/PurpleCat.png";
import catLookingUp from "./images/CatLookingUp.png";

function App() {
  const [message, setMessage] = useState("");
  const [message1, setMessage1] = useState("");
  const [page, setPage] = useState([]);
  const [title, setTitle] = useState("Alice in Wonderland");
  const [pageNum, setPageNum] = useState(1);
  const [language, setLanguage] = useState("en");
  const [difficulty, setDifficulty] = useState("Hard");

  useEffect(() => {
    axios
      .get("http://127.0.0.1:5000/message")
      .then((response) => {
        setMessage(response.data.message);
      })
      .catch((error) => {
        console.error("There was an error fetching the message!", error);
      });

    axios
      .get("http://127.0.0.1:5000/message1")
      .then((response) => {
        setMessage1(response.data.message1);
      })
      .catch((error) => {
        console.error("There was an error fetching the second message!", error);
      });

    axios
      .get("http://127.0.0.1:5000/book", {
        params: {
          pageNum: pageNum,
          title: title,
          difficulty: difficulty,
          language: language,
        },
      })
      .then((response) => {
        setPage(response.data.page);
      })
      .catch((error) => {
        console.error("There was an error fetching the page!", error);
      });
  }, [title, pageNum, language, difficulty]);

  // const wordList = ["hi", "hello", "test", "window", "chair", "wall", "floor"];
  const wordList = page;
  const WordButton = ({ initialWord, checked, id }) => {
    const [word, setWord] = useState(initialWord);
    const [isChecked, setIsChecked] = useState(checked);
    //const [englishWord, setEnglishWord] = useState("hi");

    const onChange = async () => {
      setIsChecked((prevChecked) => !prevChecked);
      try {
        const response = await axios.get("http://127.0.0.1:5000/translation", {
          params: {
            word: initialWord,
            sourceLanguage: language,
          },
        });
        const translatedWord = response.data.word;

        // Update word based on the current state
        setWord((prevWord) =>
          prevWord === translatedWord ? initialWord : translatedWord
        );
      } catch (error) {
        console.error("There was an error fetching the word", error);
      }

      // setWord((prevWord) =>
      //   prevWord === englishWord ? initialWord : englishWord
      // );
    };
    return (
      <div className="checkbox-button-container">
        <input
          type="checkbox"
          id={id}
          className="checkbox-button-input"
          checked={checked}
          onChange={onChange}
        />
        <label htmlFor={id} className="checkbox-button-label">
          {word}
        </label>
      </div>
    );
  };

  const handleClickBook = (newTitle) => {
    setTitle(newTitle);
  };

  const handleNextPage = (newPageNum) => {
    setPageNum(newPageNum);
  };

  return (
    <div className="App">
      <header>
        <div
          style={{
            position: "relative",
            display: "flex",
            flexDirection: "row",
            width: "100%",
            justifyContent: "space-between",
          }}
        >
          <i style={{ fontWeight: "bold", padding: 13, fontSize: 40 }}>
            {title}
          </i>
          <div
            style={{
              marginRight: 170,
              display: "flex",
              flexDirection: "row",
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
              }}
            >
              <select
                name="difficulty"
                id="difficulty"
                style={{
                  display: "flex",
                  backgroundColor: "#DEB4FF",
                  padding: 13,
                  flexDirection: "row",
                  fontSize: "20px",
                  fontWeight: "normal",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                  margin: 10,
                }}
                onChange={() =>
                  setDifficulty(document.getElementById("difficulty").value)
                }
              >
                <option value="hide">-- Difficulty --</option>
                <option value="Easy">Easy</option>
                <option value="Hard">Hard</option>
              </select>
            </div>
            <div
              style={{
                display: "flex",
                flexDirection: "row",
              }}
            >
              <select
                name="language"
                id="language"
                style={{
                  display: "flex",
                  backgroundColor: "#DEB4FF",
                  padding: 13,
                  flexDirection: "row",
                  fontSize: "20px",
                  fontWeight: "normal",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                  margin: 10,
                }}
                onChange={() =>
                  setLanguage(document.getElementById("language").value)
                }
              >
                <option value="hide">-- Language --</option>
                <option value="arabic">Arabic</option>
                <option value="chinese (simplified)">
                  Chinese (simplified)
                </option>
                <option value="chinese (traditional)">
                  Chinese (traditional)
                </option>
                <option value="croatian">Croatian</option>
                <option value="czech">Czech</option>
                <option value="dutch">Dutch</option>
                <option value="english">English</option>
                <option value="french">French</option>
                <option value="german">German</option>
                <option value="greek">Greek</option>
                <option value="hawaiian">Hawaiian</option>
                <option value="hebrew">Hebrew</option>
                <option value="hindi">Hindi</option>
                <option value="hmong">Hmong</option>
                <option value="hungarian">Hungarian</option>
                <option value="icelandic">Icelandic</option>
                <option value="indonesian">Indonesian</option>
                <option value="irish">Irish</option>
                <option value="italian">Italian</option>
                <option value="japanese">Japanese</option>
                <option value="korean">Korean</option>
                <option value="lao">Lao</option>
                <option value="latvian">Latvian</option>
                <option value="luxembourgish">Luxembourgish</option>
                <option value="persian">Persian</option>
                <option value="portuguese">Portuguese</option>
                <option value="punjabi">Punjabi</option>
                <option value="romanian">Romanian</option>
                <option value="russian">Russian</option>
                <option value="samoan">Samoan</option>
                <option value="serbian">Serbian</option>
                <option value="slovenian">Slovenian</option>
                <option value="spanish">Spanish</option>
                <option value="sundanese">Sundanese</option>
                <option value="swahili">Swahili</option>
                <option value="swedish">Swedish</option>
                <option value="thai">Thai</option>
                <option value="turkish">Turkish</option>
                <option value="ukrainian">Ukrainian</option>
                <option value="urdu">Urdu</option>
                <option value="vietnamese">Vietnamese</option>
                <option value="welsh">Welsh</option>
                <option value="xhosa">Xhosa</option>
                <option value="yiddish">Yiddish</option>
              </select>
            </div>
            <div
              style={{
                display: "flex",
                backgroundColor: "#DEB4FF",
                padding: 13,
                flexDirection: "row",
                fontSize: "20px",
                fontWeight: "normal",
                justifyContent: "center",
                alignItems: "center",
                borderRadius: "10px",
                margin: 10,
              }}
            >
              <div style={{ paddingRight: 20 }}>Page: </div>
              <input
                type="number"
                placeholder="1"
                min="1"
                max="1000"
                id="pageNumTextBox"
                style={{
                  fontSize: "20px",
                  color: "#240035",
                  border: 0,
                  backgroundColor: "#DEB4FF",
                  borderRadius: 7,
                }}
                onChange={() =>
                  handleNextPage(
                    document.getElementById("pageNumTextBox").value
                  )
                }
              />
            </div>

            <div
              style={{
                display: "flex",
                flexDirection: "row",
              }}
            >
              <select
                name="display"
                id="display"
                style={{
                  display: "flex",
                  backgroundColor: "#DEB4FF",
                  padding: 13,
                  flexDirection: "row",
                  fontSize: "20px",
                  fontWeight: "normal",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                  margin: 10,
                }}
                onChange={() =>
                  setDifficulty(document.getElementById("display").value)
                }
              >
                <option value="hide">-- Display --</option>
                <option value="Book">Book</option>
                <option value="Flash Cards">Flash Cards</option>
              </select>
            </div>
          </div>
        </div>
      </header>
      <content>
