import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import PurpleCat from "../images/PurpleCat.png";
import catLookingUp from "../images/CatLookingUp.png";
import { useNavigate } from "react-router-dom";
import "../css/Home.css";

function Home() {
  // const [message, setMessage] = useState("");
  // const [message1, setMessage1] = useState("");
  const [page, setPage] = useState([]);
  const [title, setTitle] = useState("Alice in Wonderland");
  const [pageNum, setPageNum] = useState(1);
  const [language, setLanguage] = useState("en");
  const [difficulty, setDifficulty] = useState("Hard");
  const [display, setDisplay] = useState("Book");
  const navigate = useNavigate(); // Hook for navigation
  const [wordDictionaryRef, setWordDictionaryRef] = useRef({});

  useEffect(() => {
    // axios
    //   .get("http://127.0.0.1:5000/message")
    //   .then((response) => {
    //     setMessage(response.data.message);
    //   })
    //   .catch((error) => {
    //     console.error("There was an error fetching the message!", error);
    //   });
    // axios
    //   .get("http://127.0.0.1:5000/message1")
    //   .then((response) => {
    //     setMessage1(response.data.message1);
    //   })
    //   .catch((error) => {
    //     console.error("There was an error fetching the second message!", error);
    //   });
    axios
      .get("http://127.0.0.1:5000/book", {
        params: {
          pageNum: pageNum,
          title: title,
          difficulty: difficulty,
          language: language,
        },
      })
      .then((response) => {
        setPage(response.data.page);
      })
      .catch((error) => {
        console.error("There was an error fetching the page!", error);
      });
  }, [title, pageNum, language, difficulty, display]);

  const wordList = page;
  // const WordButton = ({ initialWord, checked, id }) => {
  //   const [word, setWord] = useState(initialWord);
  //   const [isChecked, setIsChecked] = useState(checked);
  //   const onChange = async () => {
  //     setIsChecked((prevChecked) => !prevChecked);
  //     try {
  //       const response = await axios.get("http://127.0.0.1:5000/translation", {
  //         params: {
  //           word: initialWord,
  //         },
  //       });
  //       const translatedWord = response.data.word;
  //       setWord((prevWord) =>
  //         prevWord === translatedWord ? initialWord : translatedWord
  //       );
  //     } catch (error) {
  //       console.error("There was an error fetching the word", error);
  //     }
  //   };
  //   return (
  //     <div className="checkbox-button-container">
  //       <input
  //         type="checkbox"
  //         id={id}
  //         className="checkbox-button-input"
  //         checked={checked}
  //         onChange={onChange}
  //       />
  //       <label htmlFor={id} className="checkbox-button-label">
  //         {word}
  //       </label>
  //     </div>
  //   );
  // };


  // const WordButton = ({ initialWord, checked, id, language }) => {
  //   const [word, setWord] = useState(initialWord);
  //   const [isChecked, setIsChecked] = useState(checked);

  //   const onChange = async () => {
  //     setIsChecked((prevChecked) => !prevChecked); // Toggle checked state

  //     try {
  //       const response = await axios.get("http://127.0.0.1:5000/translation", {
  //         params: {
  //           word: initialWord,
  //         },
  //       });
  //       const translatedWord = response.data.word;

  //       // Toggle between the original word and the translated word
  //       setWord((prevWord) =>
  //         prevWord === translatedWord ? initialWord : translatedWord
  //       );
  //     } catch (error) {
  //       console.error("There was an error fetching the word", error);
  //     }
  //   };

  //   return (
  //     <div className="checkbox-button-container">
  //       <input
  //         type="checkbox"
  //         id={id}
  //         className="checkbox-button-input"
  //         checked={isChecked}
  //         onChange={onChange}
  //       />
  //       <label htmlFor={id} className="checkbox-button-label">
  //         {word}
  //       </label>
  //     </div>
  //   );
  // };


  const WordButton = ({ initialWord, id, language, wordDictionaryRef }) => {
    const [word, setWord] = useState(initialWord);
    const [localChecked, setLocalChecked] = useState(false); // Local toggle state for this button

    const onChange = async () => {
      setLocalChecked((prevChecked) => !prevChecked); // Toggle local checked state

      try {
        // Check if the word is already in the dictionary
        if (!wordDictionaryRef.current[initialWord]) {
          // If word hasn't been translated before, fetch translation
          const response = await axios.get("http://127.0.0.1:5000/translation", {
            params: {
              word: initialWord,
              sourceLanguage: language,
            },
          });

          const translatedWord = response.data.word;

          // Toggle between the original word and the translated word
          setWord((prevWord) =>
            prevWord === translatedWord ? initialWord : translatedWord
          );

          // Update the wordDictionaryRef in the parent component
          wordDictionaryRef.current[initialWord] = translatedWord; // Store the translation directly
        } else {
          // If word is already translated, just toggle between original and translated
          setWord(initialWord); // Show the original word
          delete wordDictionaryRef.current[initialWord]; // Remove from dictionary
        }
      } catch (error) {
        console.error("There was an error fetching the translation", error);
      }
    };

    // The checkbox should be checked if the word is in the dictionary (translated)
    const isChecked = wordDictionaryRef.current[initialWord] !== undefined;

    return (
      <div className="checkbox-button-container">
        <input
          type="checkbox"
          id={id}
          className="checkbox-button-input"
          checked={localChecked || isChecked} // Keep local checked state or dictionary state
          onChange={onChange}
        />
        <label htmlFor={id} className="checkbox-button-label">
          {word}
        </label>
      </div>
    );
  };




  const handleClickBook = (newTitle) => {
    setTitle(newTitle);
  };

  const handleNextPage = (newPageNum) => {
    setPageNum(newPageNum);
  };

  const handleGoToFlashCards = () => {
    navigate("/flash-cards"); // Navigate to the book shelf
  };

  return (
    <div>
      <header>
        <div
          style={{
            position: "relative",
            display: "flex",
            flexDirection: "row",
            width: "100%",
            justifyContent: "space-between",
          }}
        >
          <i style={{ fontWeight: "bold", padding: 13, fontSize: 40 }}>
            {title}
          </i>
          <div
            style={{
              marginRight: 170,
              display: "flex",
              flexDirection: "row",
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
              }}
            >
              {/* Difficulty selection component */}
              <select
                name="difficulty"
                id="difficulty"
                style={{
                  display: "flex",
                  backgroundColor: "#DEB4FF",
                  padding: 13,
                  flexDirection: "row",
                  fontSize: "20px",
                  fontWeight: "normal",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                  margin: 10,
                }}
                onChange={() =>
                  setDifficulty(document.getElementById("difficulty").value)
                }
              >
                <option value="hide">-- Difficulty --</option>
                <option value="Easy">Easy</option>
                <option value="Hard">Hard</option>
              </select>
            </div>
            {/* Language selection component */}
            <div
              style={{
                display: "flex",
                flexDirection: "row",
              }}
            >
              <select
                name="language"
                id="language"
                style={{
                  display: "flex",
                  backgroundColor: "#DEB4FF",
                  padding: 13,
                  flexDirection: "row",
                  fontSize: "20px",
                  fontWeight: "normal",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                  margin: 10,
                }}
                onChange={() =>
                  setLanguage(document.getElementById("language").value)
                }
              >
                <option value="hide">-- Language --</option>
                <option value="arabic">Arabic</option>
                <option value="chinese (simplified)">
                  Chinese (simplified)
                </option>
                <option value="chinese (traditional)">
                  Chinese (traditional)
                </option>
                <option value="croatian">Croatian</option>
                <option value="czech">Czech</option>
                <option value="dutch">Dutch</option>
                <option value="english">English</option>
                <option value="french">French</option>
                <option value="german">German</option>
                <option value="greek">Greek</option>
