import FlashCard from "../components/FlashCard";
import contentReturnBookshelfCat from "../images/contentReturnCat.png";
import happyReturnBookshelfCat from "../images/happyReturnCat.png";
import { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../css/FlashCards.css";

function FlashCards(props) {
  //const { wordListInput } = props;
  const [index, setIndex] = useState(0);
  const [showTranslation, setShowTranslation] = useState(false);
  const [currentCat, setCurrentCat] = useState(contentReturnBookshelfCat);
  const navigate = useNavigate(); // Hook for navigation
  const location = useLocation();
  const wordListFromPage = location.state?.mistakeList || [];
  //const [wordList, setWordList] = useState(wordListFromPage);
  const wordList = wordListFromPage;
  // const [wordList, setWordList] = useState([
  //   {
  //     word: "hello",
  //     translation: "hallo",
  //   },
  //   {
  //     word: "hi",
  //     translation: "hoi",
  //   },
  //   {
  //     word: "See you later",
  //     translation: "Tot de volgende keer",
  //   },
  // ]);
  console.log(wordList);

  useEffect(() => {}, [index]);

  const removeWord = () => {
    // if (wordList.length === 1) {
    //   //"No more mistakes to review", "No more mistakes to review";
    // } else {
    //   setWordList(wordList.splice(index, 1));
    //   console.log(wordList);

    if (index === wordList.length - 1) {
      setIndex(0);
    }
    if (index >= 0 && index < wordList.length) {
      wordList.splice(index, 1); // Remove 1 item at the specified index
      //updateList();  // Re-render the updated list
      setCurrentCat(happyReturnBookshelfCat);
      setTimeout(function () {
        setCurrentCat(contentReturnBookshelfCat);
      }, 2000);
    }
  };

  const handlePreviousCard = () => {
    // If current card is not the first card, go to the previous card, otherwise go to last card in deck
    setIndex(
      (prevIndex) => (prevIndex - 1 + wordList.length) % wordList.length
    );
  };

  const handleNextCard = () => {
        // If current card is not the first card, go to the previous card, otherwise go to last card in deck

    setIndex((prevIndex) => (prevIndex + 1) % wordList.length);
  };

  const handleFlipCard = () => {
    setShowTranslation(!showTranslation);
  };

  const handleReturnToShelf = () => {
    navigate("/"); // Navigate to the book shelf
  };

  const currentCard = wordList[index];

  return (
    <div class="background">
      <div class="catChair"></div>
      <img
        class="contentReturnBookshelfCat"
        src={wordList.length < 1 ? happyReturnBookshelfCat : currentCat}
        onClick={handleReturnToShelf}
      />
      <div class="flashCardButtonsContainer">
        <div className="flashCard" onClick={handleFlipCard}>
          {wordList.length < 1
            ? "All Done!"
            : showTranslation
            ? currentCard.translation
            : currentCard.word}
        </div>
        <div style={{ display: "flex", flexDirection: "row" }}>
          <div class="changeCardButton" onClick={() => handlePreviousCard()}>
            {/* left arrow */}
            &#x2190;
          </div>
          <div class="changeCardButton" onClick={() => handleNextCard()}>
            {/* right arrow */}
            &#x2192;
          </div>
        </div>
        <div class="changeCardButton" onClick={() => removeWord()}>
          Memorized!
        </div>
      </div>
    </div>
  );
}
export default FlashCards;
