import FlashCard from "../components/FlashCard";

function FlashCards() {
  function removeWord() {
    if (wordListy.length == 1) {
      "No more mistakes to review", "No more mistakes to review";
    } else {
      wordListy.splice(wordIndex, 1);
      setRefresh(!refresh);

      if (wordIndex == wordListy.length - 1) {
        setIndex(0);
      }
    }
  }

  function handleNextCard() {
    if (wordListy[wordIndex].Score == 3) {
      removeWord();
    } else {
      const newwordListy = [...wordListy];
      newwordListy[wordIndex].Score = wordListy[wordIndex].Score + 1;
      setWordListy(newwordListy);
    }
    if (wordIndex < wordListy.length - 1) {
      setIndex(wordIndex + 1);
      setEnglish(false);
      //console.log(wordListy[wordIndex]);
    } else if (wordListy.length > 0) {
      setIndex(0);
      setEnglish(false);
    } else {
      returnHome();
    }
  }
  return (
    <div
      style={{
        width: "100%",
        aspectRatio: 2,
        justifyContent: "center",
        alignItems: "center",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <FlashCard word={"hello"} translation={"hallo"}></FlashCard>
      <div style={{ display: "flex", flexDirection: "row" }}>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            textAlign: "center",
            alignItems: "center",
            fontSize: 50,
            backgroundColor: "red",
            borderRadius: 20,
            height: 150,
            width: 300,
            cursor: "pointer",
            margin: 20,
          }}
        >
          Come back to me later
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            textAlign: "center",
            alignItems: "center",
            fontSize: 50,
            backgroundColor: "green",
            borderRadius: 20,
            height: 150,
            width: 300,
            cursor: "pointer",
            margin: 20,
          }}
        >
          Memorized
        </div>
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          textAlign: "center",
          alignItems: "center",
          fontSize: 50,
          borderRadius: 20,
          height: 150,
          width: 500,
          cursor: "pointer",
        }}
      >
        Return to book shelf
      </div>
    </div>
  );
}
export default FlashCards;
