import React, { useState, useEffect, useRef } from "react";
import axios from "axios"
import catWithYarn from "../images/CatWithYarn.gif";
import PurpleCatMoving from "../images/PurpleCatMoving.gif";
import BookPage from "../components/bookPage";
import { useNavigate, useLocation } from "react-router-dom";
import "../css/Home.css";
import "../css/BookPage.css";

function Home() {
  //Get the word list from the Flash Cards page if there
  const location = useLocation();
  //const mistakeList = useRef(location.state?.mistakeList ?? []);

  // Access location.state.mistakeList and set fallback to initialMistakeList.current
  // const mistakeList = location.state?.mistakeList ?? initialMistakeList.current;
  // const mistakeList = location.state?.mistakeList || initialMistakeList;
  const [title, setTitle] = useState("Alice in Wonderland.txt");
  const [pageNum, setPageNum] = useState(1);
  const [language, setLanguage] = useState("en");
  const [difficulty, setDifficulty] = useState("Hard");
  const mistakeList = useRef([]);
  const [reload, setReload] = useState(false);
  const navigate = useNavigate(); // Hook for navigation
  const [page, setPage] = useState([]);

  useEffect(() => {
    setPage([]);
    if (location.state?.mistakeList) {
      mistakeList.current = location.state.mistakeList;
    }
    const savedTitle = sessionStorage.getItem('title');
    const savedDifficulty = sessionStorage.getItem('difficulty');
    const savedPageNum = sessionStorage.getItem('pageNum');
    const savedLanguage = sessionStorage.getItem('language')

    if (savedTitle) setPageTitle(savedPageTitle);
    if (savedDifficulty) setDifficulty(savedDifficulty);
    if (savedPageNum) setPageNum(Number(savedPageNum));
    axios
      .get("http://127.0.0.1:5000/book", {
        params: {
          pageNum: pageNum,
          title: title,
          difficulty: difficulty,
          language: language,
        },
      })
      .then((response) => {
        setPage(response.data.page);
      })
      .catch((error) => {
        console.error("There was an error fetching the page!", error);
      });
  }, [title, pageNum, language, difficulty, location.state.mistakeList]);

  const updateMistakeList = (mistake) => {
    mistakeList.current.push(mistake);
  };

  const handleClickBook = (newTitle) => {
    setTitle(newTitle);
  };

  const handleNextPage = (newPageNum) => {
    setPageNum(newPageNum);
  };

  const incrementPage = () => {
    setPageNum((prevPageNum) => {
      const newPageNum = Math.min(prevPageNum + 1, 1000); // Increment but limit to 1000
      handleNextPage(newPageNum); // Ensure the function is called with the updated page number
      return newPageNum;
    });
  };

  const decrementPage = () => {
    setPageNum((prevPageNum) => {
      const newPageNum = Math.max(prevPageNum - 1, 1); // Decrement but limit to 1
      handleNextPage(newPageNum); // Ensure the function is called with the updated page number
      return newPageNum;
    });
  };

  const handleGoToPage = (e) => {
    setPageNum(e.target.value);
  };

  const handleBlur = () => {
    // When the input loses focus, enforce the bounds (1 to 1000)
    const validPageNum = Math.max(1, Math.min(1000, pageNum));
    setPageNum(validPageNum);
    handleNextPage(validPageNum); // Update the page number if needed
  };

  const handleGoToFlashCards = () => {
    // Make sure mistake list is updated
    setReload(!reload);

    // Go to the flash cards page
    navigate("/flash-cards", {
      state: { mistakeList: mistakeList.current },
    });
  };

  return (
    <div>
      {/* Header */}
      <header>
        <div
          style={{
            position: "relative",
            display: "flex",
            flexDirection: "row",
            width: "100%",
            justifyContent: "space-between",
          }}
        >
          {/* Title */}
          <i style={{ fontWeight: "bold", padding: 13, fontSize: 40 }}>
            {title.slice(0, -4)}
          </i>
          {/* Settings Portion of the Header */}
          <div
            style={{
              marginRight: 170,
              display: "flex",
              flexDirection: "row",
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
              }}
            >
              {/* Difficulty selection component */}
              <select
                name="difficulty"
                id="difficulty"
                style={{
                  display: "flex",
                  backgroundColor: "#DEB4FF",
                  padding: 13,
                  flexDirection: "row",
                  fontSize: "20px",
                  fontWeight: "normal",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                  margin: 10,
                }}
                onChange={() =>
                  setDifficulty(document.getElementById("difficulty").value)
                }
              >
                <option value="hide">-- Difficulty --</option>
                <option value="Easy">Easy</option>
                <option value="Hard">Hard</option>
              </select>
            </div>
            {/* Language selection component */}
            <div
              style={{
                display: "flex",
                flexDirection: "row",
              }}
            >
              <select
                name="language"
                id="language"
                style={{
                  display: "flex",
                  backgroundColor: "#DEB4FF",
                  padding: 13,
                  flexDirection: "row",
                  fontSize: "20px",
                  fontWeight: "normal",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                  margin: 10,
                }}
                onChange={() =>
                  setLanguage(document.getElementById("language").value)
                }
              >
                <option value="hide">-- Language --</option>
                <option value="arabic">Arabic</option>
                <option value="chinese (simplified)">
                  Chinese (simplified)
                </option>
                <option value="chinese (traditional)">
                  Chinese (traditional)
                </option>
                <option value="croatian">Croatian</option>
                <option value="czech">Czech</option>
                <option value="dutch">Dutch</option>
                <option value="english">English</option>
                <option value="french">French</option>
                <option value="german">German</option>
                <option value="greek">Greek</option>
                <option value="hawaiian">Hawaiian</option>
                <option value="hebrew">Hebrew</option>
                <option value="hindi">Hindi</option>
                <option value="hmong">Hmong</option>
                <option value="hungarian">Hungarian</option>
                <option value="icelandic">Icelandic</option>
                <option value="indonesian">Indonesian</option>
                <option value="irish">Irish</option>
                <option value="italian">Italian</option>
                <option value="japanese">Japanese</option>
                <option value="korean">Korean</option>
                <option value="lao">Lao</option>
                <option value="latvian">Latvian</option>
                <option value="luxembourgish">Luxembourgish</option>
                <option value="persian">Persian</option>
                <option value="portuguese">Portuguese</option>
                <option value="punjabi">Punjabi</option>
                <option value="romanian">Romanian</option>
                <option value="russian">Russian</option>
                <option value="samoan">Samoan</option>
                <option value="serbian">Serbian</option>
                <option value="slovenian">Slovenian</option>
                <option value="spanish">Spanish</option>
                <option value="sundanese">Sundanese</option>
                <option value="swahili">Swahili</option>
                <option value="swedish">Swedish</option>
                <option value="thai">Thai</option>
                <option value="turkish">Turkish</option>
                <option value="ukrainian">Ukrainian</option>
                <option value="urdu">Urdu</option>
                <option value="vietnamese">Vietnamese</option>
                <option value="welsh">Welsh</option>
                <option value="xhosa">Xhosa</option>
                <option value="yiddish">Yiddish</option>
              </select>
            </div>
            {/* Page selection component */}
            <div
              style={{
                display: "flex",
                backgroundColor: "#DEB4FF",
                padding: 13,
                flexDirection: "row",
                fontSize: "20px",
                fontWeight: "normal",
                justifyContent: "center",
                alignItems: "center",
                borderRadius: "10px",
                margin: 10,
              }}
            >
              <div style={{ paddingRight: 20 }}>Page: </div>
              <input
                type="number"
                placeholder={pageNum}
                value={pageNum}
                min="1"
                max="1000"
                id="pageNumTextBox"
                style={{
                  fontSize: "20px",
                  color: "#240035",
                  border: 0,
                  backgroundColor: "#DEB4FF",
                  borderRadius: 7,
                }}
                onChange={
                  handleGoToPage
                }
                onBlur={handleBlur}
              />
            </div>
            {/* Button to Go to FlashCards */}
            <div
              style={{
                display: "flex",
                backgroundColor: "#C972FF",
                padding: 13,
                flexDirection: "row",
                fontSize: "20px",
                fontWeight: "normal",
                justifyContent: "center",
                alignItems: "center",
                borderRadius: "10px",
                margin: 10,
                cursor: "pointer",
              }}
              onClick={() => handleGoToFlashCards()}
            >
              Review Mistakes
            </div>
          </div>
        </div>
      </header>
      <content>
        {/* Bookshelf */}
        <div class="bookshelf" style={{ fontSize: 15, font: "Georgia" }}>
          {/* Top Shelf */}
          <div class="shelf">
            <div class="book-stack">
              <div
                class="book"
                style={{ backgroundColor: "#DEB4FF", width: "95%" }}
                onClick={() => handleClickBook("Alice in Wonderland.txt")}
              >
                <i>Alice in Wonderland</i>
              </div>
              <div
                class="book"
                style={{ backgroundColor: "#A16FB8", width: "87%" }}
                onClick={() => handleClickBook("Peter Pan.txt")}
              >
                <i>Peter Pan</i>
              </div>
              <div
                class="book"
                style={{ backgroundColor: "#C972FF", width: "95%" }}
                onClick={() => handleClickBook("Anne of Green Gables.txt")}
              >
                <i>Anne of Green Gables</i>
              </div>
            </div>
            <div class="book-stack-right">
              <div
                class="upright-book"
                style={{
                  backgroundColor: "#DEB4FF",
                  height: "60%",
                  marginRight: "18px",
                  transform: "rotate(20deg)",
                }}
              ></div>
              <div
                class="upright-book"
                style={{ backgroundColor: "#A16FB8", height: "80%" }}
              ></div>
              <div
                class="upright-book"
                style={{ backgroundColor: "#C972FF", height: "70%" }}
              ></div>
            </div>
          </div>
          <div class="shelf-wood"></div>
          {/* Shelf 2*/}
          <div class="shelf">
            <div class="book-stack"></div>
            <div class="book-stack">
              <div
