import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [message, setMessage] = useState("");
  const [message1, setMessage1] = useState("");

  useEffect(() => {
    axios
      .get("http://127.0.0.1:5000/message")
      .then((response) => {
        setMessage(response.data.message);
      })
      .catch((error) => {
        console.error("There was an error fetching the message!", error);
      });

    axios
      .get("http://127.0.0.1:5000/message1")
      .then((response) => {
        setMessage1(response.data.message1);
      })
      .catch((error) => {
        console.error("There was an error fetching the second message!", error);
      });
  }, []);

  return (
    <div className="App">
      <h1>Message from Flask:</h1>
      <p>{message}</p>
      <h1>Second Message from Flask:</h1>
      <p>{message1}</p>
      <header>
        <select name="difficulty" id="difficulty">
          <option value="easy">easy</option>
          <option value="medium">medium</option>
          <option value="hard">hard</option>
        </select>
        <div style="display: flex; margin-left:100px; flex-direction: row;">
          <select
            name="language"
            id="language"
            style="padding: 10px; margin: 10px; background-color: #DEB4FF; border-radius: 50px;"
          >
            <option value="english">{english}</option>
            <option value="spanish">spanish</option>
            <option value="french">french</option>
          </select>
        </div>
      </header>
    </div>
  );
}

export default App;
