import FlashCard from "../components/FlashCard";
import { useState, useEffect } from "react";

function FlashCards(props) {
  const { wordListInput } = props;
  const [index, setIndex] = useState(0);
  const [wordList, setWordList] = useState([
    {
      word: "hello",
      translation: "hallo",
    },
    {
      word: "hi",
      translation: "hoi",
    },
  ]);
  console.log(wordList);

  useEffect(() => {
    // axios
    //   .get("http://127.0.0.1:5000/message")
    //   .then((response) => {
    //     setMessage(response.data.message);
    //   })
    //   .catch((error) => {
    //     console.error("There was an error fetching the message!", error);
    //   });
    // axios
    //   .get("http://127.0.0.1:5000/message1")
    //   .then((response) => {
    //     setMessage1(response.data.message1);
    //   })
    //   .catch((error) => {
    //     console.error("There was an error fetching the second message!", error);
    //   });
    axios
      .get("http://127.0.0.1:5000/book", {
        params: {
          pageNum: pageNum,
          title: title,
          difficulty: difficulty,
          language: language,
        },
      })
      .then((response) => {
        setPage(response.data.page);
      })
      .catch((error) => {
        console.error("There was an error fetching the page!", error);
      });
  }, [title, pageNum, language, difficulty, display]);

  const removeWord = () => {
    if (wordList.length === 1) {
      //"No more mistakes to review", "No more mistakes to review";
    } else {
      setWordList(wordList.splice(index, 1));
      console.log(wordList);

      if (index === wordList.length - 1) {
        setIndex(0);
      }
    }
  };

  const handleNextCard = () => {
    if (index < wordList.length - 1) {
      setIndex(index + 1);
    } else {
      setIndex(0);
    }
  };

  return (
    <div
      style={{
        width: "100%",
        aspectRatio: 2,
        justifyContent: "center",
        alignItems: "center",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <FlashCard
        word={wordList[index].word}
        translation={wordList[index].translation}
      ></FlashCard>
      <div style={{ display: "flex", flexDirection: "row" }}>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            textAlign: "center",
            alignItems: "center",
            fontSize: 50,
            backgroundColor: "red",
            borderRadius: 20,
            height: 150,
            width: 300,
            cursor: "pointer",
            margin: 20,
          }}
        >
          Come back to me later
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            textAlign: "center",
            alignItems: "center",
            fontSize: 50,
            backgroundColor: "green",
            borderRadius: 20,
            height: 150,
            width: 300,
            cursor: "pointer",
            margin: 20,
          }}
          onClick={() => removeWord()}
        >
          Memorized
        </div>
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          textAlign: "center",
          alignItems: "center",
          fontSize: 50,
          borderRadius: 20,
          height: 150,
          width: 500,
          cursor: "pointer",
        }}
        onClick={() => handleNextCard()}
      >
        Return to book shelf
      </div>
    </div>
  );
}
export default FlashCards;
