// WordButton.js
import React, { useState } from "react";
import axios from "axios"; // Ensure axios is imported
import WordButton from "../components/WordButton";

const Paragraph = ({ initialWord, id, updateMistakeList, language }) => {
  const [word, setWord] = useState(initialWord);
  const [isChecked, setIsChecked] = useState(false); // Track checkbox state locally

  const onChange = async () => {
    setIsChecked((prevChecked) => !prevChecked); // Toggle checked state

    const prevWord = word;
    setWord("Loading ...");
    try {
      // fetch translation
      const response = await axios.get(
        "https://languagelearningbackend.onrender.com/translation",
        {
          params: {
            word: initialWord,
            sourceLanguage: language,
          },
        }
      );

      const translatedWord = response.data.word;

      // Toggle between the original word and the translated word
      setWord(prevWord === translatedWord ? initialWord : translatedWord);
      if (!isChecked) {
        updateMistakeList({
          word: initialWord,
          translation: translatedWord,
        });
      }
    } catch (error) {
      console.error("There was an error fetching the translation", error);
    }
  };

  return (
    <div class="paragraph">
      {word.map((item, index) => (
        <WordButton
          initialWord={item}
          id={index}
          updateMistakeList={updateMistakeList}
          language={language}
        />
      ))}
    </div>
  );
};

export default Paragraph; // Export the component
