import React, { useState, useEffect, setPage, useRef } from "react";
import axios from "axios";
import catWithYarn from "../images/CatWithYarn.gif";
import PurpleCatMoving from "../images/PurpleCatMoving.gif";
import BookPage from "../components/bookPage";
import { useNavigate, useLocation } from "react-router-dom";
import "../css/Home.css";
import "../css/BookPage.css";

function Home() {
  // Get the word list from the home page
  // const location = useLocation();
  // const wordList = location.state?.mistakeList || [];
  const [title, setTitle] = useState("Alice in Wonderland.txt");
  const [pageNum, setPageNum] = useState(1);
  const [language, setLanguage] = useState("english");
  const [difficulty, setDifficulty] = useState("Hard");
  const mistakeList = useRef([]);
  const [reload, setReload] = useState(false);
  const navigate = useNavigate(); // Hook for navigation
  const [page, setPage] = useState([]);
  const location = useLocation();
  const [reloaded, setReloaded] = useState(true);

  if (reloaded) {
    const savedTitle = sessionStorage.getItem("title");
    const savedDifficulty = sessionStorage.getItem("difficulty");
    const savedPageNum = sessionStorage.getItem("pageNum");
    const savedLanguage = sessionStorage.getItem("language");
    const savedPage = sessionStorage.getItem("page");

    if (savedTitle) setTitle(String(savedTitle));
    if (savedDifficulty) setDifficulty(String(savedDifficulty));
    if (savedPageNum) setPageNum(Number(savedPageNum));
    if (savedLanguage) setLanguage(String(savedLanguage));
    if (savedPage) setPage(Array(savedPage));
    setReloaded(false);
  }

  useEffect(() => {
    if (location.state?.mistakeList) {
      mistakeList.current = location.state.mistakeList;
    }
    // setPage([]);
    // axios
    //   .get("http://127.0.0.1:5000/book", {
    //     params: {
    //       pageNum: pageNum,
    //       title: title,
    //       difficulty: difficulty,
    //       language: language,
    //     },
    //   })
    //   .then((response) => {
    //     setPage(response.data.page);
    //   })
    //   .catch((error) => {
    //     console.error("There was an error fetching the page!", error);
    //   });
    setPage([]);
    if (location.state?.mistakeList) {
      mistakeList.current = location.state.mistakeList;
    }

    axios
      .get("http://127.0.0.1:5000/book", {
        params: {
          pageNum: pageNum,
          title: title,
          difficulty: difficulty,
          language: language,
        },
      })
      .then((response) => {
        setPage(response.data.page);
      })
      .catch((error) => {
        console.error("There was an error fetching the page!", error);
      });
    // Save settings
    sessionStorage.setItem("title", title);
    sessionStorage.setItem("difficulty", difficulty);
    sessionStorage.setItem("pageNum", pageNum);
    sessionStorage.setItem("language", language);
    sessionStorage.setItem("page", page);
  }, [title, pageNum, language, difficulty]);

  //const wordList = page;

  const updateMistakeList = (mistake) => {
    mistakeList.current.push(mistake);
  };

  // const WordButton = ({ initialWord, id, language, updateMistakeList }) => {
  //   const [word, setWord] = useState(initialWord);
  //   const [isChecked, setIsChecked] = useState(false); // Track checkbox state locally

  //   const onChange = async () => {
  //     setIsChecked((prevChecked) => !prevChecked); // Toggle checked state

  //     const prevWord = word;
  //     setWord("Loading ...");
  //     try {
  //       // fetch translation
  //       const response = await axios.get("http://127.0.0.1:5000/translation", {
  //         params: {
  //           word: initialWord,
  //           sourceLanguage: language,
  //         },
  //       });

  //       const translatedWord = response.data.word;

  //       // Toggle between the original word and the translated word
  //       setWord(prevWord === translatedWord ? initialWord : translatedWord);

  //       updateMistakeList({
  //         word: initialWord,
  //         translation: translatedWord,
  //       });
  //       // setWordDictionary((prev) => [
  //       //   ...prev, // Spread the existing items
  //       //   { word: initialWord, translation: translatedWord }, // Add the new word object
  //       // ]);
  //     } catch (error) {
  //       console.error("There was an error fetching the translation", error);
  //     }
  //   };

  //   return (
  //     <div className="checkbox-button-container">
  //       <input
  //         type="checkbox"
  //         id={id}
  //         className="checkbox-button-input"
  //         checked={isChecked} // Make checkbox checked based on dictionary state
  //         onChange={onChange}
  //       />
  //       <label htmlFor={id} className="checkbox-button-label">
  //         {word}
  //       </label>
  //     </div>
  //   );
  // };

  const handleClickBook = (newTitle) => {
    setTitle(newTitle);
  };

  const handleNextPage = (newPageNum) => {
    setPageNum(newPageNum);
  };

  const incrementPage = () => {
    setPageNum((prevPageNum) => {
      const newPageNum = Math.min(prevPageNum + 1, 1000); // Increment but limit to 1000
      handleNextPage(newPageNum); // Ensure the function is called with the updated page number
      return newPageNum;
    });
  };

  const decrementPage = () => {
    setPageNum((prevPageNum) => {
      const newPageNum = Math.max(prevPageNum - 1, 1); // Decrement but limit to 1
      handleNextPage(newPageNum); // Ensure the function is called with the updated page number
      return newPageNum;
    });
  };

  const handleGoToPage = (e) => {
    // const value = e.target.value;
    // if (value >= 1 && value <= 1000) {
    //   setPageNum(value);
    //   handleNextPage(value); // Call the function with the new page number
    // }

    setPageNum(e.target.value);
  };

  const handleBlur = () => {
    // When the input loses focus, enforce the bounds (1 to 1000)
    const validPageNum = Math.max(1, Math.min(1000, pageNum));
    setPageNum(validPageNum);
    handleNextPage(validPageNum); // Update the page number if needed
  };

  const handleGoToFlashCards = () => {
    // Make sure mistake list is updated
    setReload(!reload);

    // Go to the flash cards page
    navigate("/flash-cards", {
      state: { mistakeList: mistakeList.current },
    });
  };

  return (
    <div>
      {/* Header */}
      <header>
        <div
          style={{
            position: "relative",
            display: "flex",
            flexDirection: "row",
            width: "100%",
            backgroundColor: "red",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {/* Title */}
          <i
            style={{
              position: "fixed",
              fontWeight: "bold",
              padding: 13,
              fontSize: 40,
              left: 0,
            }}
          >
            {title.slice(0, -4)}
          </i>
          {/* Settings Portion of the Header */}
          <div
            style={{
              marginRight: 170,
              display: "flex",
              flexDirection: "row",
              backgroundColor: "blue",
            }}
          >
            {/* <div
              style={{
                display: "flex",
                flexDirection: "row",
              }}
            > */}
            {/* Difficulty selection component */}
            {/* <select
                name="difficulty"
                id="difficulty"
                style={{
                  display: "flex",
                  backgroundColor: "#DEB4FF",
                  padding: 13,
                  flexDirection: "row",
                  fontSize: "20px",
                  fontWeight: "normal",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                  margin: 10,
                }}
                value={difficulty}
                onChange={() =>
                  setDifficulty(document.getElementById("difficulty").value)
                }
              >
                <option value="Easy">Easy</option>
                <option value="Hard">Hard</option>
              </select> */}
            {/* </div> */}
            {/* Language selection component */}
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                backgroundColor: ""
              }}
            >
              <select
                name="language"
                id="language"
                style={{
                  display: "flex",
                  backgroundColor: "#DEB4FF",
                  padding: 13,
                  flexDirection: "row",
                  fontSize: "20px",
                  fontWeight: "normal",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                  margin: 10,
                }}
                value={language}
                onChange={() =>
                  setLanguage(document.getElementById("language").value)
                }
              >
                <option value="english">English</option>
                <option value="arabic">Arabic</option>
                <option value="chinese (simplified)">
                  Chinese (simplified)
                </option>
                <option value="chinese (traditional)">
                  Chinese (traditional)
                </option>
                <option value="croatian">Croatian</option>
                <option value="czech">Czech</option>
                <option value="dutch">Dutch</option>
                <option value="english">English</option>
                <option value="french">French</option>
                <option value="german">German</option>
                <option value="greek">Greek</option>
                <option value="hawaiian">Hawaiian</option>
