import FlashCard from "../components/FlashCard";
import { useState, useEffect } from "react";

function FlashCards(props) {
  const { wordListInput } = props;
  const [index, setIndex] = useState(0);
  const [showTranslation, setShowTranslation] = useState(false);
  const [wordList, setWordList] = useState([
    {
      word: "hello",
      translation: "hallo",
    },
    {
      word: "hi",
      translation: "hoi",
    },
    {
      word: "See you later",
      translation: "Tot de volgende keer",
    },
  ]);
  console.log(wordList);

  useEffect(() => {}, [index]);

  const removeWord = () => {
    if (wordList.length === 1) {
      //"No more mistakes to review", "No more mistakes to review";
    } else {
      setWordList(wordList.splice(index, 1));
      console.log(wordList);

      if (index === wordList.length - 1) {
        setIndex(0);
      }
    }
  };

  const handleNextCard = () => {
    setShowTranslation(false);
    setIndex((prevIndex) => (prevIndex + 1) % wordList.length);
  };

  const handleFlipCard = () => {
    setShowTranslation(!showTranslation);
  };

  const currentCard = wordList[index];

  return (
    <div style={{
      width: "100%",
      height: "100%",
      backgroundColor: "red",

    }}>
    <div
      style={{
        width: "100%",
        aspectRatio: 2,
        justifyContent: "center",
        alignItems: "center",
        display: "flex",
        flexDirection: "column",
        backgroundColor: "red",
      }}
    >
      <div
        className="flash-card"
        onClick={handleFlipCard}
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          fontSize: 75,
          backgroundColor: "#e7c0ff",
          borderRadius: 40,
          padding: 100,
          //marginTop: 40,
          height: 400,
          cursor: "pointer",
          minWidth: 400,
        }}
      >
        {showTranslation ? currentCard.translation : currentCard.word}
      </div>
      <div style={{ display: "flex", flexDirection: "row" }}>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            textAlign: "center",
            alignItems: "center",
            fontSize: 50,
            backgroundColor: "#A16FB8",
            borderRadius: 20,
            height: 100,
            width: 180,
            cursor: "pointer",
            margin: 20,
          }}
          onClick={() => handleNextCard()}
        >
          &#x2190;
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            textAlign: "center",
            alignItems: "center",
            fontSize: 50,
            backgroundColor: "#A16FB8",
            borderRadius: 20,
            height: 100,
            width: 180,
            cursor: "pointer",
            margin: 20,
          }}
          onClick={() => handleNextCard()}
        >
          &#x2192;
        </div>
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          textAlign: "center",
          alignItems: "center",
          fontSize: 50,
          borderRadius: 20,
          height: 100,
          width: 400,
          cursor: "pointer",
          backgroundColor: "#71b86f",
        }}
      >
        Memorized!
      </div>
    </div>
    </div>
  );
}
export default FlashCards;
