import React, { useState, useEffect, memo, TouchableOpacity } from "react";
import axios from "axios";

function App() {
  const [message, setMessage] = useState("");
  const [message1, setMessage1] = useState("");
  const [page, setPage] = useState([]);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:5000/message")
      .then((response) => {
        setMessage(response.data.message);
      })
      .catch((error) => {
        console.error("There was an error fetching the message!", error);
      });

    axios
      .get("http://127.0.0.1:5000/message1")
      .then((response) => {
        setMessage1(response.data.message1);
      })
      .catch((error) => {
        console.error("There was an error fetching the second message!", error);
      });

    axios
      .get("http://127.0.0.1:5000/book", {
        params: {
          pageNum: 10,
          title: "Alice",
        },
      })
      .then((response) => {
        setPage(response.data.page);
      })
      .catch((error) => {
        console.error("There was an error fetching the page!", error);
      });
  }, []);

  // const wordList = ["hi", "hello", "test", "window", "chair", "wall", "floor"];
  const wordList = page;
  const WordButton = memo(function WordButton({ word, on }) {
    // const [word, setWord] = useState(ogWord);
    const [active, setActive] = useState(on);
    const handleClickButton = () => {
      console.log("button clicked");
      // mistakeUpdater({
      //   English: englishWord,
      //   Score: 0,
      //   realText: ogWord,
      //   oldIndex: oldIndex,
      // });

      console.log("setting as active button");
      if (active == true) {
        setActive(false);
        // setWord(ogWord);
      } else {
        setActive(true);
        // setWord(englishWord);
      }

      // stemList.current[wordList[index].oldIndex].Score = 1;
      console.log("set as active button");
    };
    return (
      <div
        class="button"
        // class={active ? "pressed-button" : "button"}
        onPress={handleClickButton}
      >
        <h2>{word}</h2>
      </div>
