import { useState } from "react";

export default function FlashCard(props) {
  const { word, translation } = props;
  const [displayedWord, setDisplayedWord] = useState(word);



  const handleClick = () => {
    if (displayedWord == word) {
      setDisplayedWord(translation);
    } else {
      setDisplayedWord(word);
    }
  };
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        fontSize: 100,
        backgroundColor: "#e7c0ff",
        borderRadius: 40,
        padding: 100,
        margin: 20,
        height: 400,
        cursor: "pointer",
      }}
      onClick={() => handleClick(word, translation)}
    >
      {displayedWord}
    </div>
  );
}
