import { React, useState, useEffect, forwardRef } from "react";
import axios from "axios";
import WordButton from "../components/WordButton";
import PageTurn from "../images/PageTurn.png";

const BookPage = forwardRef(
  (
    {
      title,
      pageNum,
      language,
      difficulty,
      temporaryMistakeList,
      setPageNum,
      handleNextPage,
    },
    ref
  ) => {
    const [page, setPage] = useState([]);

    // Get the page from the backend
    useEffect(() => {
      setPage([]);
      axios
        .get("http://127.0.0.1:5000/book", {
          params: {
            pageNum: pageNum,
            title: title,
            difficulty: difficulty,
            language: language,
          },
        })
        .then((response) => {
          setPage(response.data.page);
        })
        .catch((error) => {
          console.error("There was an error fetching the page!", error);
        });
    }, [title, pageNum, language, difficulty]);

    const updateMistakeList = (mistake) => {
      temporaryMistakeList.current.push(mistake);
    };

    const incrementPage = () => {
      setPageNum((prevPageNum) => {
        const newPageNum = Math.min(prevPageNum + 1, 1000); // Increment but limit to 1000
        handleNextPage(newPageNum); // Ensure the function is called with the updated page number
        return newPageNum;
      });
    };

    const decrementPage = () => {
      setPageNum((prevPageNum) => {
        const newPageNum = Math.max(prevPageNum - 1, 1); // Decrement but limit to 1
        handleNextPage(newPageNum); // Ensure the function is called with the updated page number
        return newPageNum;
      });
    };

    return (
      <div class="pageContainer">
        <div class="page">
          {page.length > 1 ? (
            page.map((item, index) => (
              <WordButton
                initialWord={item}
                id={index}
                updateMistakeList={updateMistakeList}
              />
            ))
          ) : (
            <WordButton
              initialWord={"Loading ..."}
              id={1}
              updateMistakeList={updateMistakeList}
            />
          )}
          <div style={{ width: "100%", height: 50 }}></div>
        </div>
        <div class="PageTurnLeftContainer">
          <div class="PageBeneathLeft"></div>
          <img
            class="PageTurnLeft"
            src={PageTurn}
            alt="PageTurnLeft"
            onClick={() => decrementPage()}
          />
        </div>
        <div class="PageTurnContainer">
          <div class="PageBeneath"></div>
          <img
            class="PageTurn"
            src={PageTurn}
            alt="PageTurn"
            onClick={() => incrementPage()}
          />
        </div>
      </div>
    );
  }
);

export default BookPage; // Export the component
