import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [message, setMessage] = useState("");
  const [message1, setMessage1] = useState("");

  useEffect(() => {
    axios
      .get("http://127.0.0.1:5000/message")
      .then((response) => {
        setMessage(response.data.message);
      })
      .catch((error) => {
        console.error("There was an error fetching the message!", error);
      });

    axios
      .get("http://127.0.0.1:5000/message1")
      .then((response) => {
        setMessage1(response.data.message1);
      })
      .catch((error) => {
        console.error("There was an error fetching the second message!", error);
      });
  }, []);

  return (
    <div className="App">
      <header>
        <strong>Language Learning Book</strong>
        <div style={{ display: "flex", marginLeft: 100, flexDirection: "row" }}>
          <select
            name="difficulty"
            id="difficulty"
            style={{
              padding: 10,
              margin: 10,
              backgroundColor: "#DEB4FF",
              borderRadius: 50,
            }}
          >
            <option value="easy">easy</option>
            <option value="medium">medium</option>
            <option value="hard">hard</option>
          </select>
        </div>
        <div style={{ display: "flex", marginLeft: 100, flexDirection: "row" }}>
          <select
            name="language"
            id="language"
            style={{
              padding: 10,
              margin: 15,
              backgroundColor: "#DEB4FF",
              borderRadius: 5,
            }}
          >
            <option value="dutch">dutch</option>
            <option value="spanish">spanish</option>
            <option value="french">french</option>
          </select>
        </div>
      </header>
      <content>
        <div class="bookshelf">
          <div class="shelf">
            <div class="book-stack">
              <div
                class="book"
                style={{ backgroundColor: "#DEB4FF", width: "90%" }}
              >
                <h1>Book 1</h1>
              </div>
              <div
                class="book"
                style={{ backgroundColor: "#A16FB8", width: "87%" }}
              >
                <h1>Book 2</h1>
              </div>
              <div
                class="book"
                style={{ backgroundColor: "#C972FF", width: "95%" }}
              >
                <h1>Book 3</h1>
              </div>
            </div>
            <div class="book-stack-right">
              <div
                class="upright-book"
                style={{
                  backgroundColor: "#DEB4FF",
                  height: "60%",
                  marginRight: "18px",
                  transform: "rotate(20deg)",
                }}
              ></div>
              <div
                class="upright-book"
                style={{ backgroundColor: "#A16FB8", height: "80%" }}
              ></div>
              <div
                class="upright-book"
                style={{ backgroundColor: "#C972FF", height: "70%" }}
              ></div>
            </div>
          </div>
          <div class="shelf-wood"></div>
          <div class="shelf">
            <div class="book-stack">
              {/* <Image
                        class="cat"
                        src="{{ url_for('static', filename='images/PurpleCat.png') }}"
  />*/}
            </div>
            <div class="book-stack">
              <div
                class="book"
                style={{ backgroundColor: "#A16FB8", width: "80%" }}
              >
                <h1>Book 4</h1>
              </div>
              <div
                class="book"
                style={{ backgroundColor: "#C972FF", width: "90%" }}
              >
                <h1>Book 5</h1>
              </div>
              <div
                class="book"
                style={{ backgroundColor: "#DEB4FF", width: "87%" }}
              >
                <h1>Book 6</h1>
              </div>
              <div
                class="book"
                style={{ backgroundColor: "#A16FB8", width: "95%" }}
              >
                <h1>Book 7</h1>
              </div>
            </div>
          </div>
          <div class="shelf-wood"></div>
          <div class="shelf">
            <div
              class="book-stack-right"
              style={{ justifyContent: "flex-start" }}
            >
              <div
                class="upright-book"
                style={{ backgroundColor: "#C972FF", height: "80%" }}
              ></div>
              <div
                class="upright-book"
                style={{ backgroundColor: "#A16FB8", height: "70%" }}
              ></div>
              <div
                class="upright-book"
                style={{ backgroundColor: "#DEB4FF", height: "90%" }}
              ></div>
              <div
                class="upright-book"
                style={{
                  backgroundColor: "#A16FB8",
                  height: "80%",
                  marginLeft: "12px",
                  transform: "rotate(-10deg)",
                }}
              ></div>
            </div>
            <div class="book-stack">
              <div
                class="book"
                style={{ backgroundColor: "#DEB4FF", width: "85%" }}
              >
                <h1>Book 8</h1>
              </div>
              <div
                class="book"
                style={{ backgroundColor: "#C972FF", width: "95%" }}
              >
                <h1>Book 9</h1>
              </div>
              <div
                class="book"
                style={{ backgroundColor: "#A16FB8", width: "96%" }}
              >
                <h1>Book 10</h1>
              </div>
            </div>
          </div>
          <div class="shelf-wood"></div>
          <div class="shelf"></div>
          <div class="shelf-wood"></div>
          <div class="shelf"></div>
        </div>
        <div class="page">
          <p>{message1}</p>
          <p>{message}</p>
        </div>
      </content>
    </div>
  );
}

export default App;
